pragma solidity ^0.7.1;

contract StudentRecord{
    struct Student{
        uint rollno;
        string name;
        string class;
    }
    address public principle;
    uint counter=0;
    mapping(uint=>Student) list;
    modifier onlyPrinciple{
        require(msg.sender==principle,"You are not the principle");
        _;
    }
    modifier costs{
        require(msg.value>1 ether,"Fees need to be greater than 1 ether");
        _;
    }
    event StudentAdded(uint indexed _rollno,string _name,string _class);
    event FeesPaid(address indexed paidBy,uint paidOfRollNo,uint amount);
    constructor(){
        principle=msg.sender;
    }
    function addStrudent(string memory _name,string memory _class) public onlyPrinciple{
        counter+=1;
        list[counter]=Student(counter,_name,_class);
        emit StudentAdded(counter,list[counter].name,list[counter].class);
    }
    function showStudentName(uint _rollNo) public view returns(string memory){
        return list[_rollNo].name;
    }
    function payFees(uint _rollNo) public payable costs{
        emit FeesPaid(msg.sender,_rollNo,msg.value);
    }
}
